import { useEffect, useState } from 'react';
import axios from "axios";

function App() {
  const [addSection,setAddSection] = useState(false)
  const [editSection,setEditSection] = useState(false)
  const [formData,setFormData] = useState({
    RoomCode: "",
    Name: "",
    Temperature: "",
    ImageURL: "",
    FireStatus: "",
    GasStatus: "",
  })
  const [formDataEdit,setFormDataEdit] = useState({
    RoomCode: "",
    Name: "",
    Temperature: "",
    ImageURL: "",
    FireStatus: "",
    GasStatus: "",
  })
  const [dataList,setDataList] = useState([])

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:8090/api/listHouse");
      setDataList(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleOnChange = (e)=>{
    const {value,name} = e.target
    setFormData((preve)=>{
        return{
          ...preve,
          [name] : value
        }
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post("http://localhost:8090/api/addHouse", formData);
      
      if (response.data.success) {
        setAddSection(false);
        alert(response.data.message);
        fetchData();
        setFormData({
          RoomCode: "",
          Name: "",
          ImageURL: "",
          Temperature: "",
          FireStatus: "",
          GasStatus: ""
        });
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async(id)=>{
    const data = await axios.delete("http://localhost:8090/api/deleteHouse/"+id)
    
      if(data.data.success){
        fetchData()
        alert(data.data.message)
      }
  }

  


  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <th>Room Code</th>
            <th>Name</th>
            <th>Temperature</th>
            <th>Image URL</th>
            <th>Fire Status</th>
            <th>Gas Status</th>
          </tr>
        </thead>
        <tbody>
          {dataList.length ? (
            dataList.map((el) => (
              <tr key={el.RoomCode}>
                <td>{el.RoomCode}</td>
                <td>{el.Name}</td>
                <td>{el.Temperature}</td>
                <td>{el.ImageURL}</td>
                <td>{el.FireStatus}</td>
                <td>{el.GasStatus}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No data</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default App;
